mapper.select:
select ma_no, m_no, ma_zipcode, ma_addr1, ma_addr2, ma_addr_type, ma_regdate
from member_address
where m_no = #{no}

mapper.update:
update member_address


		ma_zipcode = #{zipcode},


		ma_addr1 = #{addr1},


		ma_addr2 = #{addr2},


		ma_addr_type = #{addrType},




		ma_no = #{addrNo}


		and m_no = #{memNo}


		and ma_addr_type = #{addrType}
